mapper.select:
select e.id, e.ser_numb, e.rom_id,r.numb as rom_numb, r.rom_name, e.eq_class_id,c.type_name, e.buy_time, e.repair_times, e.update_time, e.`state`
from equipment e
left join roms r on e.rom_id = r.id
left join equipment_class c on c.id = e.eq_class_id


    and e.rom_id = #{romId}


    and r.numb = #{romNumb}


    and r.rom_name = #{romName}


    and e.ser_numb = #{serNumb}


    and e.eq_class_id = #{eqClassId}


     and c.type_name = #{typeName}


    and e.state = #{state}


order by e.update_time desc

  limit #{pageNo},#{pageSize}

select count(*)
from equipment e
left join roms r on e.rom_id = r.id
left join equipment_class c on c.id = e.eq_class_id


        and e.rom_id = #{romId}


        and r.numb = #{romNumb}


        and r.rom_name = #{romName}


        and e.ser_numb = #{serNumb}


        and e.eq_class_id = #{eqClassId}


        and c.type_name = #{typeName}


        and e.state  = #{state}

select e.id, e.ser_numb, e.rom_id,r.numb,r.rom_name, e.eq_class_id,c.type_name, e.buy_time, e.repair_times, e.update_time, e.`state`
from equipment e
left join roms r on e.rom_id = r.id
left join equipment_class c on c.id = e.eq_class_id
where e.rom_id <> 0

        and r.numb = #{romNumb}


        and r.rom_name = #{romName}


        and e.ser_numb = #{serNumb}


        and e.eq_class_id = #{eqClassId}


        and c.type_name = #{typeName}


        and e.state = #{state}

order by e.update_time desc
limit #{pageNo},#{pageSize}

select count(*)
from equipment e
left join roms r on e.rom_id = r.id
left join equipment_class c on c.id = e.eq_class_id
where e.rom_id <> 0

        and r.numb = #{romNumb}


        and r.rom_name = #{romName}


        and e.ser_numb = #{serNumb}


        and e.eq_class_id = #{eqClassId}


        and c.type_name = #{typeName}


        and e.state = #{state}

order by e.update_time desc

select e.id, e.ser_numb, e.rom_id,r.numb,r.rom_name, e.eq_class_id,c.type_name, e.buy_time, e.repair_times, e.update_time, e.`state`
from equipment e
left join roms r on e.rom_id = r.id
left join equipment_class c on c.id = e.eq_class_id
where e.rom_id = 0

    and e.ser_numb = #{serNumb}


    and e.eq_class_id = #{eqClassId}


    and c.type_name = #{typeName}


    and e.state  = #{state}

order by e.update_time desc
limit #{pageNo},#{pageSize}

select count(*)
from equipment e
left join roms r on e.rom_id = r.id
left join equipment_class c on c.id = e.eq_class_id
where e.rom_id = 0

    and e.ser_numb = #{serNumb}


    and e.eq_class_id = #{eqClassId}


    and c.type_name = #{typeName}


    and e.state = #{state}

order by e.update_time desc
